import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: [],
      activeItem: {
        id: null,
        title: "",
        completed: false,
      },
      editing: false,
    };
  }

  componentWillMount() {
    this.fetchTasks();
  }

  fetchTasks = () => {
    console.log("Fetching...");

    fetch("http://127.0.0.1:8000/api/task-list/")
      .then((response) => response.json())
      .then((data) =>
        this.setState({
          todoList: data,
        })
      );
  };

  render() {
    var tasks = this.state.todoList;
    return (
      <div className="container">
        <div id="task-container">
          <div id="form-wrapper">
            <form id="form">
              <div className="flex-wrapper">
                <div style={{ flex: 7 }}>
                  <input
                    type="text"
                    className="form-control"
                    id="title"
                    placeholder="Task Todo..."
                    aria-describedby="emailHelp"
                  />
                </div>

                <div style={{ flex: 1 }}>
                  <button id="submit" type="submit" className="btn btn-primary">
                    Submit
                  </button>
                </div>
              </div>
            </form>
          </div>

          <div id="list-wrapper">
            {tasks.map((task, index) => {
              return (
                <div
                  style={{ flex: 7 }}
                  key={index}
                  className="task-wrapper flex-wrapper"
                >
                  <div>
                      <span>{task.title}</span>
                  </div>

                  <div style={{flex}}>
                      <button clasName="btn btn-sm btn-outline-info">Edit</button>
                  </div>
                  <div style={{flex:1}}>
                  <button clasName="btn btn-sm btn-danger">Delete</button>

                  </div>
                </div>


              );
            })}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
